package _02_QuickQuiz;

public class _02_Expression {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=10,y=2,z=3;
		System.out.println((float)(x-y)/z);
		
		int a=5,b=7,c=9;
		System.out.println((float)(b*b-4*a*c)/2*a); //explicit type casting
		int v=5,u=9;
		System.out.println((v*v)-(u*u));
		int d=7;
		System.out.println((a*b)-d);
		
	}

}
